/**
 * EnSentRec 类，用于解析英文识别结果<br>
 * 支持内核资源eng.rec.splp.0.1<br>
 * $Id: EnSentRec.js 4935 2014-08-21 09:03:38Z zhiyuan.liang $
 *
 * @namespace 英文识别结果类
 */
chivox.EnSentRec = function(data){
    this.data = data;
}
/**
 * 获得用户设置的评分参数（部分参数经过了过滤处理）
 */
chivox.EnSentRec.prototype.getParams = function(){
    return this.data.params;
}
/**
 * 获得本次评分的录音id。<br>
 * 在startRecord的时候，服务器生成的一个唯一的id，并回传给客户端
 */
chivox.EnSentRec.prototype.getRecordId = function(){
    return this.data.recordId;
}
/**
 * 获得本次请求的录音文件地址
 * @return {string}
 */
chivox.EnSentRec.prototype.getAudioUrl = function(){
    return this.data.audioUrl;
}
/**
 * 判断是否识别错误
 * @return {boolean}
 */
chivox.EnSentRec.prototype.hasError = function(){
    var r = false;
    if (typeof this.data.result == "undefined" || typeof this.data.result["err"] != "undefined" || typeof this.data.result["error"] != "undefined" || typeof this.data.result["errID"] != "undefined") {
        r = true;
    }
    return r;
}
/**
 * 获得错误码。如果不存在错误，则无返回值。
 * @return {int}
 */
chivox.EnSentRec.prototype.getErrorCode = function(){
    if (this.hasError()) {
        if (typeof this.data.result != "undefined" && this.data.result["errID"] != "undefined") {
            return this.data.result["errID"];
        } else {
            return this.data["error"];
        }
    }
    return;
}
/**
 * 本次的识别结果置信度
 * @return {int}
 */
chivox.EnSentRec.prototype.getConf = function(){
    return this.data.result["conf"];
}
/**
 * 本次的识别结果
 * @return {int}
 */
chivox.EnSentRec.prototype.getRec = function(){
    return this.data.result["rec"];
}
