<script>
	export default {
		props: {
			dataAllOne:{
					type: Object,
					default: []
			},
			bodyColor:{
					type:String,
					default: ''
			}
		},
		data() {
			return {
				totalContent: false,//新增total-content区域的判断条件
        generalPerformance: true,
        languageSkill: true,
        textResult: true,
        generalPerformance1: true,
        generalPerformance2: true,
        generalPerformance3: true,

        dataVersion: '',//新增报告版本 1.0 1.1 为老版本  1.2为新版本
        dataType: '',
        dataStatus: '',
        outNumber: '',
        levelSrc: '',
        unitSrc: '',
        CTOne: [],CTTwo: [],CTThree: [],
        teacher_extraDetail: [],
        newTeacherExtraDetail: [],
        teacherAvatar: '',
        goalNew: [],
        curriculumNumObj: [],
        learningContentNumObj: [],
        evalutionHeight: false,
        LanguageCh: false,
        teacherComment: '',
        teacherCommentEg: '',
        teacherCommentCh: '',
        reportContentTrial:[],
        reportContentCT:[],
        curriculumBuoyTop: '',
        curriculumBuoyRight: '',
        ifLong: false,
        ifLongEn: false,
        ifLongCh: false,
        dataIf: true,

    		//total区域部分新增判定
        totalContentSkill: false,
        totalContentPerformance: false,
        totalContentEvalution: false,
        totalContentMask1: false,
        totalContentMask2: false,
        totalContentMask3: false,
        totalContentSkillNoHead: false //total skill-content去除顶部
			}
		},
		watch: {
			dataAllOne:{
					handler(curVal,oldVal){
							//此处setTimeout是为了跳出Vue生命周期 与watch的特性有关
							curVal && setTimeout(()=> {
									this.render(curVal)
							});
							this.dataIf = false;
							setTimeout(() => {
									this.judgeHeight()
							});
	　　　　　　　},
					deep:true
			}
		},
		mounted(){
			try{
					if( !this.dataAllOne ) throw "dataAllOne出错了";
			}
			catch(err){
					alert(err)
			}
			window.onresize = () => {
					return (() => {
							this.judgeHeight()
					})()
			}
			this.judgeHeight()
			this.getTypeStatus()
			if(this.dataVersion == '1.0' ||this.dataVersion == '1.1'){
					this.totalContent = false
					console.log('这是老版本 render 所有方法都不变')
					if(this.dataType == 1){
							this.getDataOne()
							this.getCtExamDetail()
							// 静态资源加载
							this.getFutureLearning()
							this.getFutureLearningLeft()
							this.getFutureLearningRight()
							this.getTeacherComment()
							this.getBuoy()
							if(this.dataStatus == 1){
									// this.generalPerformance = false;
									this.generalPerformance1 = false;
									this.generalPerformance2 = false;
									this.generalPerformance3 = false;
									this.languageSkill = false;
									// this.getReportTypeOne()
							}
					
							if(this.dataStatus == 4){
									this.languageSkill = false;
									this.getGeneralPerformance()
							}
					}
			
					if(this.dataType == 2){
							this.getDataOne()
							this.textResult = false;
					
							// 静态资源加载
							this.getFutureLearning()
							this.getFutureLearningLeft()
							this.getFutureLearningRight()
							this.getTeacherComment()
							this.getBuoy()
							if(this.dataStatus == 1){
									this.generalPerformance1 = false;
									this.generalPerformance2 = false;
									this.generalPerformance3 = false;
									this.getReportTypeOne()
							}
					
							if(this.dataStatus == 4){
									this.getReportTypeTwo()
									this.getGeneralPerformance()
							}
					}
			
					if(this.dataType == 0){
							this.getDataOne()
							this.textResult = false;
							this.getGeneralPerformance()
					
							// 静态资源加载
							this.getFutureLearning()
							this.getFutureLearningLeft()
							this.getFutureLearningRight()
							this.getTeacherComment()
							this.getBuoy()
					
							if(this.dataStatus == 2){
									this.getReportTypeThree()
									
							}
					
							if(this.dataStatus == 3){
									this.getReportTypeTwo()
					
							}
					}
			}else if(this.dataVersion == '1.2'){
					this.totalContent = true //启用新版本区域
					console.log('这是新版本 render')
					if(this.dataType == 1){
							this.getDataOne()
							this.getCtExamDetail()
							// 静态资源加载
							this.getFutureLearning()
							this.getFutureLearningLeft()
							this.getFutureLearningRight()
							this.getTeacherComment()
							this.getBuoy()
							if(this.dataStatus == 1){
									this.totalContentMask3 = true
									this.languageSkill = false;
									// this.getReportTypeOne()
							}
					
							if(this.dataStatus == 4){
									this.totalContentPerformance = true
									this.totalContentEvalution = true
									this.languageSkill = false;
									this.getGeneralPerformance()
							}
					}
			
					if(this.dataType == 2){
							this.getDataOne()
							this.textResult = false;
					
							// 静态资源加载
							this.getFutureLearning()
							this.getFutureLearningLeft()
							this.getFutureLearningRight()
							this.getTeacherComment()
							this.getBuoy()
							if(this.dataStatus == 1){
									// 老版本的方法不用了
									this.totalContentSkill = true
									this.totalContentMask1 = true
									this.getReportTypeTwo()  //language skill 第二种方法 不需要补全
							}
					
							if(this.dataStatus == 4){
									this.totalContentSkill = true
									this.totalContentPerformance = true
									this.totalContentEvalution = true
									
									this.getReportTypeTwo()
									this.getGeneralPerformance()
							}
					}
			
					if(this.dataType == 0){
							this.getDataOne()
							this.textResult = false;
							this.getGeneralPerformance()
					
							// 静态资源加载
							this.getFutureLearning()
							this.getFutureLearningLeft()
							this.getFutureLearningRight()
							this.getTeacherComment()
							this.getBuoy()
					
							if(this.dataStatus == 2){
									this.getReportTypeFive() //新加的方法
									this.totalContentSkill = true
									this.totalContentPerformance = true
									this.totalContentEvalution = true
									this.totalContentMask2 = true
									this.totalContentSkillNoHead = true
							}
					
							if(this.dataStatus == 3){
									this.getReportTypeTwo()
									this.totalContentSkill = true
									this.totalContentPerformance = true
									this.totalContentEvalution = true
									this.totalContentMask2 = true
									this.totalContentSkillNoHead = true
									
							}
					}
			}
		},
		methods: {
			judgeHeight(){
					// console.log('**********');
					let evalutionInsideContent = document.querySelector(".evalution-inside-content")
					// console.log(evalutionInsideContent.clientHeight)
					// console.log(evalutionInsideContent.scrollHeight)
					if(evalutionInsideContent.scrollHeight <= 220){
							this.ifLong = true
					}else{
							this.ifLong = false
					}
					// console.log('this.ifLong',this.ifLong)
					console.log('resize 了')
			},
			
			// 英译汉切换button
			changeLanguage(){
					if(this.LanguageCh){
					this.LanguageCh = !this.LanguageCh
					this.teacherComment = this.evalutionInsideEn
					// this.judgeHeight()
					}else{
					this.LanguageCh = !this.LanguageCh
					this.teacherComment = this.evalutionInsideCh
					// this.judgeHeight()
					}
					//   let that = this
					setTimeout(() => {
							this.judgeHeight()
					});
			},
			// 展开页面高度
			expandHeight(){
					if(this.evalutionHeight){
					this.evalutionHeight = !this.evalutionHeight
					}else{
					this.evalutionHeight = !this.evalutionHeight
					}
			},
			// 获取type 和 status 进行最初级判断
			getTypeStatus(){
					this.dataType = this.dataAllOne.reportType;
					console.log("dataType",this.dataType)
					this.dataStatus = this.dataAllOne.status;
					console.log("dataStatus",this.dataStatus)
					if(this.dataAllOne.version){
					console.log('version存在')
					this.dataVersion = this.dataAllOne.version;
					}else{
					console.log('version不存在')
					this.dataVersion = '1.0';
					}
					console.log("dataVersion",this.dataVersion)
			},
			// 一些基础信息
			getDataOne(){
					this.outNumber = this.dataAllOne.outNumber + '%'
					// level等级
					let dataLevel = this.dataAllOne.level;
					// level图片src
					if(dataLevel && dataLevel.length){
					this.levelSrc = require('../img/level/level-' + dataLevel.replace(/[^0-9]/g,'') + '.png');
					}else{
					this.levelSrc = ''
					}
					// unit单元
					let dataUnit = this.dataAllOne.unit;
					// unit图片src
					if(dataUnit && dataUnit.length){
					this.unitSrc = require('../img/unit/unit-' + dataUnit.replace(/[^0-9]/g,'') + '.png');
					}else{
					this.unitSrc = ''
					}

					

			},
			// 小恐龙部分数据获取
			getCtExamDetail(){
					let ctExamDetail = this.dataAllOne.ctExamDetail;
					// console.log("ctExamDetail",ctExamDetail)
					if(ctExamDetail && ctExamDetail.length){
					this.textResult = true

							for(let i = 0; i < ctExamDetail.length; i++){
							//CTOne text-result 第123道题
							if(i < 3 && i >= 0){
									this.CTOne.push(ctExamDetail[i]);
							}

							//CTTwo text-result 第456道题
							if(i >= 3 && i < 6){
									this.CTTwo.push(ctExamDetail[i]);
							}

							//CTThree text-result 第789道题
							if(i >= 6 && i < 9){
									this.CTThree.push(ctExamDetail[i]);
							}
							}
					}else{
					this.textResult = false
					}
			},
			// 教师feedback和表情部分
			getGeneralPerformance(){
					if(this.dataAllOne.teacherScore && this.dataAllOne.teacherScore.length){
							let teacherExtraDetail = JSON.parse(this.dataAllOne.teacherScore)["gradeDtos"];
							let objDetailCh , objDetailEn , objSymbol , ObjSrc , objName;
							let regularTeacherExtraDetail = [
																									{
																									"name": "confidence" 
																									},
																									{
																											"name": "participation"
																									},
																									{
																											"name": "behavior"
																									}
																							]
									for(let j = 0; j < regularTeacherExtraDetail.length; j++){

											for(let i = 0; i < teacherExtraDetail.length; i++){
													if(regularTeacherExtraDetail[j].name === teacherExtraDetail[i].name){
															// has = true;
															if(teacherExtraDetail[i].name == 'participation'){
																	objSymbol = '参与度';
																	objName = 'Participation';
															}else if(teacherExtraDetail[i].name == 'behavior'){
																	objSymbol = '行为表现';
																	objName = 'Behavior';
															}else if(teacherExtraDetail[i].name == 'confidence'){
																	objSymbol = '自信心';
																	objName = 'Confidence';
															}
											
															if(teacherExtraDetail[i].score == 4 || teacherExtraDetail[i].score == 5){
																	objDetailCh = '表现优异';
																	objDetailEn = 'Excellent';
																	ObjSrc = require('../img/extraDetail-1.png')
															}else if(teacherExtraDetail[i].score == 3){
																	objDetailCh = '平均水平';
																	objDetailEn = 'Onlevel';
																	ObjSrc = require('../img/extraDetail-2.png')
															}else if(teacherExtraDetail[i].score == 1 || teacherExtraDetail[i].score == 2){
																	objDetailCh = '有待提高';
																	objDetailEn = 'Needs Work';
																	ObjSrc = require('../img/extraDetail-3.png')
															}

															regularTeacherExtraDetail[j] = teacherExtraDetail[i]
															regularTeacherExtraDetail[j].objSymbol = objSymbol
															regularTeacherExtraDetail[j].objName = objName
