			getBuoy(){
					let dataLevel = this.dataAllOne.level;

					if(dataLevel && dataLevel.length){
					let middleLevel = 'middle-' + dataLevel.replace(/[^0-9]/g,'');

					let curriculumBuoy = this.$refs[middleLevel].childNodes[0]
					let middleCenter = this.$refs[middleLevel].childNodes[2]
					curriculumBuoy.style.cssText="display: inline-block";

					let buoyTop = - (curriculumBuoy.offsetHeight + 30);
					// console.log('buoyTop:',buoyTop,'curriculumBuoy.offsetHeight:',curriculumBuoy.offsetHeight,'buoyRight:',buoyRight,'curriculumBuoy.offsetWidth/2:',curriculumBuoy.offsetWidth/2,'middleCenter.offsetWidth/2:',middleCenter.offsetWidth/2,'curriculumBuoy:',curriculumBuoy,'middleCenter:',middleCenter)
					let buoyRight = - (curriculumBuoy.offsetWidth/2 - middleCenter.offsetWidth/2);

					this.curriculumBuoyTop = buoyTop
					this.curriculumBuoyRight = buoyRight
