					this.getCtExamDetail()
				}			
				//CT
				if(this.dataStatus === 1 && this.dataType === 2){
					this.title1 = true
					this.generalPerformance = true
					this.skillContent = true
					this.evalutionBg = true
					this.placementDescriptions = false
					
					this.getReportTypeTwo()
					
				}
				//esl tc3.0无定级
				if(this.dataStatus === 2 && this.trialType === 3){
					this.title2 = true
					this.generalPerformance = true
					this.skillContent = true
					this.performance = true
					this.evalution = true
					this.languageSkill = true
					this.placementDescriptions = false

					this.checkStudentName(this.dataAllTwo.studentName)
					this.getReportTypeTwo()
					this.getTeacherComment()
					this.getGeneralPerformance()
					
				}
				//esl CT + TC3.0高level
				if(this.dataStatus === 4 && this.trialType === 3 && this.dataType === 2){
					this.title1 = true
					this.generalPerformance = true
					this.skillContent = true
					this.performance = true
					this.evalution = true
					this.placementDescriptions = false

					this.getReportTypeTwo()
					this.getTeacherComment()
					this.getGeneralPerformance()
				}
				//esl CT + TC3.0低level
				if(this.dataStatus === 4 && this.trialType === 3 && this.dataType === 1){
					this.title1 = true
					this.testResult = true		
					this.generalPerformance = true
					this.skillContent = true
					this.performance = true
					this.evalution = true
					this.placementDescriptions = false

					this.getReportTypeTwo()
					this.getTeacherComment()
					this.getGeneralPerformance()
					this.getCtExamDetail()

				}	
				// MC TC3.0无定级
				if(this.dataStatus === 2 && this.trialType === 2){
					this.title2 = true
					this.generalPerformance = true
					this.skillContent = true
					this.performance = true
					this.evalution = true
					this.languageSkill = true
					this.placementDescriptions = false

					this.checkStudentName(this.dataAllTwo.studentName)
					this.getReportTypeTwo()
					this.getTeacherComment()
					this.getGeneralPerformance()
					
				}		
				//MC CT + TC3.0 高level
				if(this.dataStatus === 4 && this.dataType === 2 && this.trialType === 2){
					this.title1 = true
					this.generalPerformance = true
					this.skillContent = true
					this.performance = true
					this.evalution = true
					// this.placementDescriptions = true
					this.learningPlan = true

					this.getReportTypeTwo()
					this.getTeacherComment()
					this.getGeneralPerformance()
					this.getFutureLearning()
					setTimeout(()=>{
						this.getBuoy()
					},100)
				}
				//MC CT + TC3.0 低level
				if(this.dataStatus === 4 && this.dataType === 1 && this.trialType === 2){
					this.title1 = true
					this.generalPerformance = true
					this.skillContent = true
					this.testResult = true
					this.performance = true
					this.evalution = true
					// this.placementDescriptions = true
					this.learningPlan = true

					this.getCtExamDetail()
					this.getReportTypeTwo()
					this.getTeacherComment()
					this.getGeneralPerformance()
					this.getFutureLearning()
					setTimeout(()=>{
						this.getBuoy()
					},100)
				}			
				//MC TC1.0 无定级
				if(this.dataStatus === 2 && this.trialType === 1){
					this.title2 = true
					this.generalPerformance = true
					this.evalution = true
					this.languageSkill = true
					this.placementDescriptions = false

					this.checkStudentName(this.dataAllTwo.studentName)
					this.getTeacherComment()
					
				}
				//MC CT + TC1.0 高level
				if(this.dataStatus === 4 && this.dataType === 2 && this.trialType === 1){
					this.title1 = true
					this.generalPerformance = true
					this.skillContent = true
					// this.performance = true
					this.evalution = true
					// this.placementDescriptions = true
					this.learningPlan = true

					this.getReportTypeTwo()
					this.getTeacherComment()
					this.getGeneralPerformance()
					this.getFutureLearning()
					setTimeout(()=>{
						this.getBuoy()
					},100)
				}
				//MC CT + TC1.0 低level
				if(this.dataStatus === 4 && this.dataType === 1 && this.trialType === 1){
					this.title1 = true
					this.generalPerformance = true
					this.testResult = true
					// this.skillContent = true
					// this.performance = true
					this.evalution = true
					// this.placementDescriptions = true
					this.learningPlan = true

					this.getCtExamDetail()
					this.getReportTypeTwo()
					this.getTeacherComment()
					this.getFutureLearning()
					setTimeout(()=>{
						this.getBuoy()
					},100)
				}
			},
			checkStudentName(name){
				if(name.length > 10){
					this.longName = true
				}
			},
			// 获取type 和 status 进行最初级判断
			getTypeStatus(){
				this.dataType = this.dataAllTwo.reportType;
				console.log("dataType",this.dataType)
				this.dataStatus = this.dataAllTwo.status;
				console.log("dataStatus",this.dataStatus)
				this.trialType = this.dataAllTwo.trialType
				console.log("trialType",this.trialType)
			},
			// 小恐龙部分数据获取
			getCtExamDetail(){
					let ctExamDetail = this.dataAllTwo.ctExamDetail;
					// console.log("ctExamDetail",ctExamDetail)
					if(ctExamDetail && ctExamDetail.length){
					this.textResult = true

							for(let i = 0; i < ctExamDetail.length; i++){
							//CTOne text-result 第123道题
							if(i < 3 && i >= 0){
									this.CTOne.push(ctExamDetail[i]);
							}

							//CTTwo text-result 第456道题
							if(i >= 3 && i < 6){
									this.CTTwo.push(ctExamDetail[i]);
							}

							//CTThree text-result 第789道题
							if(i >= 6 && i < 9){
									this.CTThree.push(ctExamDetail[i]);
							}
							}
					}else{
					this.textResult = false
					}
			},
			// 静态资源加载
			getFutureLearning(){
					let futureLearning = this.dataAllTwo.futureLearning;
					this.goalNew = futureLearning['goal']

			},
			getFutureLearningLeft(){
					let futureLearning = this.dataAllTwo.futureLearning;
					let curriculumNum = futureLearning['curriculumNum']
					this.curriculumNumObj = eval('(' + curriculumNum + ')');
			},
			getFutureLearningRight(){
					let futureLearning = this.dataAllTwo.futureLearning;
					let learningContentNum = futureLearning['learningContentNum']
					this.learningContentNumObj = eval('(' + learningContentNum + ')');
			},
			getBuoy(){
				console.log('this.getBuoy()!!!')
				let dataLevel = this.dataAllTwo.level;

				if(dataLevel && dataLevel.length){
					let middleLevel = 'middle-' + dataLevel.replace(/[^0-9]/g,'');
					let curriculumBuoy = this.$refs[middleLevel].childNodes[0]
					let middleCenter = this.$refs[middleLevel].childNodes[2]
					
					curriculumBuoy.style.cssText="display: inline-block";

					let buoyTop = - (curriculumBuoy.offsetHeight + 30);
					let buoyRight = - (curriculumBuoy.offsetWidth/2 - middleCenter.offsetWidth/2);

					this.curriculumBuoyTop = buoyTop
					this.curriculumBuoyRight = buoyRight
				}
					
			},
			getData(){
				this.outNumber = this.dataAllTwo.outNumber + '%'
				this.dataLevel = this.dataAllTwo.level;
				this.dataUnit = this.dataAllTwo.unit;

				this.level = this.dataAllTwo.level && this.dataAllTwo.level.substring(1,this.dataAllTwo.level.length)
				this.unit = this.dataAllTwo.unit && this.dataAllTwo.unit.substring(1,this.dataAllTwo.unit.length)

			},
			getReportTypeTwo(){
					let reportContent = this.dataAllTwo.reportContent;
					if(reportContent && reportContent.length > 0){
						for(let i = 0; i < reportContent.length; i++){
					
							let redHas ,greenHas;
							let barProportion = (reportContent[i]['score']/reportContent[i]['total']) * 100 ;
							if(reportContent[i]['total'] === 0){
								barProportion = 0;
							}
							if( barProportion === 100){
								barProportion = 98
							}
							if( barProportion <= 14.825 ){
									redHas = false;
									greenHas = true;
							}else if(barProportion && barProportion >= 85.714){
									greenHas = false;
									redHas = true;
							}else{
									greenHas = true;
									redHas = true;
							}
							reportContent[i].redHas = redHas
							reportContent[i].greenHas = greenHas
							reportContent[i].barProportion = barProportion
							// reportContent[i].needArrIcon = false
							this.reportContentTrial.push(reportContent[i])
						}
					}
					
					
			},
			// 教师feedback和表情部分
			getGeneralPerformance(){
					if(this.dataAllTwo.teacherScore && this.dataAllTwo.teacherScore.length){
							let teacherExtraDetail = JSON.parse(this.dataAllTwo.teacherScore)["gradeDtos"];
							let objDetailCh , objDetailEn , objSymbol , ObjSrc , objName;
							let regularTeacherExtraDetail = [
																									{
																									"name": "confidence" 
																									},
																									{
																											"name": "participation"
																									},
																									{
																											"name": "behavior"
																									}
																							]
									for(let j = 0; j < regularTeacherExtraDetail.length; j++){

											for(let i = 0; i < teacherExtraDetail.length; i++){
													if(regularTeacherExtraDetail[j].name === teacherExtraDetail[i].name){
															// has = true;
															if(teacherExtraDetail[i].name == 'participation'){
																	objSymbol = '参与度';
																	objName = 'Participation';
															}else if(teacherExtraDetail[i].name == 'behavior'){
																	objSymbol = '行为表现';
																	objName = 'Behavior';
															}else if(teacherExtraDetail[i].name == 'confidence'){
																	objSymbol = '自信心';
																	objName = 'Confidence';
															}
											
															if(teacherExtraDetail[i].score == 4 || teacherExtraDetail[i].score == 5){
																	objDetailCh = '表现优异';
																	objDetailEn = 'Excellent';
																	ObjSrc = require('../img/reportTwo/extraDetail-1.png')
															}else if(teacherExtraDetail[i].score == 3){
																	objDetailCh = '平均水平';
																	objDetailEn = 'On level';
																	ObjSrc = require('../img/reportTwo/extraDetail-2.png')
															}else if(teacherExtraDetail[i].score == 1 || teacherExtraDetail[i].score == 2){
																	objDetailCh = '有待提高';
																	objDetailEn = 'Needs Work';
																	ObjSrc = require('../img/reportTwo/extraDetail-3.png')
															}

															regularTeacherExtraDetail[j] = teacherExtraDetail[i]
															regularTeacherExtraDetail[j].objSymbol = objSymbol
															regularTeacherExtraDetail[j].objName = objName
															regularTeacherExtraDetail[j].objDetailCh = objDetailCh
															regularTeacherExtraDetail[j].objDetailEn = objDetailEn
															regularTeacherExtraDetail[j].ObjSrc = ObjSrc
															break;
													}
											}
									
									}
									this.newTeacherExtraDetail = regularTeacherExtraDetail
					}
					

			},
			getTeacherComment(){
					this.evalutionInsideEn = this.dataAllTwo.teacherCommentEg
					this.evalutionInsideCh = this.dataAllTwo.teacherCommentCh
					this.teacherAvatar = this.dataAllTwo.teacherAvatar
					this.teacherComment = this.evalutionInsideEn
			},
			// 英译汉切换button
			changeLanguage(){
				console.log('changeLanguage')
					if(this.LanguageCh){
					this.LanguageCh = !this.LanguageCh
					this.teacherComment = this.evalutionInsideEn
					}else{
					this.LanguageCh = !this.LanguageCh
					this.teacherComment = this.evalutionInsideCh
					}
			},
			
		}
	}
</script>
