export default {
  install (Vue) {
    let tpl = null
    let vm = null
    let el = null
    Vue.prototype.$modelShow = (config) => {
      tpl = Vue.extend(config.model)
      if (vm == null) {
        vm = new tpl({
          propsData: {
            config: config
          }
        })
        el = vm.$mount().$el
        document.body.appendChild(el)
      }
    }
    Vue.prototype.$modelClose = () => {
      document.body.removeChild(el)
      vm = null
    }
  }
}
