export default {
  install (Vue) {
    Vue.directive('gscroll', {
      bind: (el) => {
        el.addEventListener('mousewheel', (e) => {
          el.scrollLeft += e.deltaY || e.detail
        }, false)
        el.addEventListener('DOMMouseScroll', (e) => {
          el.scrollLeft += e.deltaY || e.detail
        }, false)
      }
    })
  }
}

