<template lang="jade">
  .back-btn(@click.stop="goBack")
</template>
<script>
  import untils from '../../utils/_untils.js'
  export default {
    data: function () {
      return {}
    },
    components: {},
    computed: {},
    mounted () {
    },
    props: ["source"],
    methods: {
      goBack(){
        //回退
        if (this.source == -1) {
          history.go(-1)
          return
        }
        //到首页
        if (this.source == 0) {
          untils.gotoFirstPage()
          return
        }
        this.$playSound("click")
      }
    }
  }
</script>

<style lang="stylus" scoped>
  .back-btn
    position: fixed
    left: 40px
    top: 18px
    height: 58px
    width: 58px
    background: url("../../img/common/back.png") no-repeat
    cursor: pointer
    z-index: 10
    &:hover
      background: url("../../img/common/back-hover.png") no-repeat
    &:active
      background: url("../../img/common/back-pressed.png") no-repeat
</style>
