<template lang="jade">
  .courseware
    top-part
    .courseware-wrapper(v-if="isStatic")
      .courseware-container
        .ppt-module
          .ppt-box-wrap
            .ppt-title {{title_english}}
            .ppt-box-wrapper
              .next-page(@click = "nextPage", v-show="isNextPageShow")
              .last-page(@click = "lastPage", v-show="isLastPageShow")
              .ppt-box
                .ppt-list#j-ppt-list
                  .ppt-null(v-if="pptUrlCollection.length == 0", v-show="isPptNullShow")
                    img(src="../../img/coursewarefail.png")
                    p 哎呀，课件打不开了
                    .refresh-ppt-page-btn(@click="reload") 刷新重试
                  img.pptpage(v-else, v-for="item in pptUrlCollection", :src="item", style="display:none")
            .page(v-show="!isPptNullShow")
              span {{currPage + 1}}
              span  /
              span {{totalPage}}
    .dynamic-container(v-if="isDynamic")
      .ppt-title {{title_english}}
      iframe.iframe-itself( frameborder="0", scrolling="no", :src="cwUrl")
</template>
<script>
  import axios from 'axios'
  import cookies from 'cookies-js'
  import TopPart from '../common/top_part.vue'
  export default {
    data() {
      return {
        title_chinese: '',
        title_english: '',
        currPage: 0,
        totalPage: 0,
        pptList: {},
        isNextPageShow: true,
        isLastPageShow: false,
        onlineClassId: this.$route.params.onlineClassId,
        lessonNum: this.$route.params.lessonNum,
        lessonId: this.$route.params.lessonId,
        pptUrlCollection: [],
        isPptNullShow: false,
        isStatic: false,
        isDynamic: false,
        cwUrl: ''
      }
    },
    components: {
      TopPart
    },
    mounted() {
      this.$nextTick(() => {
        this.checkIsDynamicOrStatic()
      })
      this.title_english = 'Lesson ' + this.lessonNum
    },
    methods: {
      reload() {
        window.location.reload()
      },
      showNoPPTHint() {
        //用静态课件的 ”无课件UI“
        this.isStatic = true

        this.isPptNullShow = true
        this.isNextPageShow = false
      },
      //检查是否是动态课件，如果不是则用静态课件接口。
      checkIsDynamicOrStatic() {
        axios.get('/rest/learninggw/api/pc/path/summercamp/getPPTByOnlineClassId', {
          params: {
            studentId: cookies.get("studentId"),
            onlineClassId: this.onlineClassId,
            lessonId: this.lessonId
          }
        })
          .then((res) => {
            if(res.data.code != 200) {
              this.showNoPPTHint()
              return
            }
            if(res.data.data.type == 0) {
            //只有静态课件
              this.isStatic = true
              let staticPptList = res.data.data.dbycoursewareUrl[0].mainStatic3Urls
              if(staticPptList) {
                this.isShowControl = true
                for(let i = 0; i < staticPptList.length; i++) {
                  this.pptUrlCollection.push( staticPptList[i] )
                }
                setTimeout(() => {
                  this.initPptControl()
                }, 0)
              } else {
                this.showNoPPTHint()
              }
            } else {
              if( !res.data.data.editorPreviewUrl ) {
                this.showNoPPTHint()
                return
              }
              this.cwUrl = res.data.data.editorPreviewUrl[0]
              this.isDynamic = true
            }
          })
          .catch((err) => {
            this.showNoPPTHint()
          })
      },
      initPptControl() {
        this.pptList = document.querySelectorAll('.pptpage')
        this.pptList[0].style.display = "inline-block"
        this.totalPage = this.pptList.length
        this.currPage = 0
      },
      nextPage() {
        this.currPage++
        this.pptControl(this.currPage)
      },
      lastPage() {
        this.currPage--
        this.pptControl(this.currPage)
      },
      pptControl(currPage) {
        if (currPage >= this.totalPage - 1 ) {
          this.currPage = this.totalPage - 1
          this.isNextPageShow = false
          this.showPptBycurrPage(currPage)
          return
        } else if (currPage <= 0) {
          this.currPage = 0
          this.isLastPageShow = false
          this.showPptBycurrPage(currPage)
          return
        } else {
          this.isNextPageShow = true
          this.isLastPageShow = true
          this.showPptBycurrPage(currPage)
        }
        
      },
      showPptBycurrPage(cp) {
        for(let i = 0; i < this.pptList.length; i++) {
          this.pptList[i].style.display = "none"
        }
        this.pptList[cp].style.display = "inline-block"
      }
    }
  }
</script>

