<template lang="jade">
  .equipment-type
    .equipment-type-each(@click="openShow()",:class="selectPartId==''?'equipment-type-each-selected':''")
      img.equipment-type-each-icon(v-show="selectPartId!=''",src="../../../img/dino_mall/all.png")
      img.equipment-type-each-icon(v-show="selectPartId==''",src="../../../img/dino_mall/all_selected.png")
      p.equipment-type-each-text All
    .equipment-type-each(@click="openShow('HEAD_PARTS')",:class="selectPartId=='HEAD_PARTS'?'equipment-type-each-selected':''")
      img.equipment-type-each-icon(v-show="selectPartId!='HEAD_PARTS'",src="../../../img/dino_mall/head.png")
      img.equipment-type-each-icon(v-show="selectPartId=='HEAD_PARTS'",src="../../../img/dino_mall/head_selected.png")
