    .equipment-type-each(@click="openShow('FACE_PARTS')",:class="selectPartId=='FACE_PARTS'?'equipment-type-each-selected':''")
      img.equipment-type-each-icon(v-show="selectPartId!='FACE_PARTS'",src="../../../img/dino_mall/face.png")
      img.equipment-type-each-icon(v-show="selectPartId=='FACE_PARTS'",src="../../../img/dino_mall/face_selected.png")
      p.equipment-type-each-text Face
    .equipment-type-each(@click="openShow('BODY_PARTS')",:class="selectPartId=='BODY_PARTS'?'equipment-type-each-selected':''")
      img.equipment-type-each-icon(v-show="selectPartId!='BODY_PARTS'",src="../../../img/dino_mall/clothes.png")
      img.equipment-type-each-icon(v-show="selectPartId=='BODY_PARTS'",src="../../../img/dino_mall/clothes_selected.png")
      p.equipment-type-each-text Clothes
    .equipment-type-each(@click="openShow('HAND_PARTS')",:class="selectPartId=='HAND_PARTS'?'equipment-type-each-selected':''")
      img.equipment-type-each-icon(v-show="selectPartId!='HAND_PARTS'",src="../../../img/dino_mall/hand.png")
      img.equipment-type-each-icon(v-show="selectPartId=='HAND_PARTS'",src="../../../img/dino_mall/hand_selected.png")
      p.equipment-type-each-text Hand
    .equipment-type-each(@click="openShow('LEG_PARTS')",:class="selectPartId=='LEG_PARTS'?'equipment-type-each-selected':''")
      img.equipment-type-each-icon(v-show="selectPartId!='LEG_PARTS'",src="../../../img/dino_mall/leg.png")
      img.equipment-type-each-icon(v-show="selectPartId=='LEG_PARTS'",src="../../../img/dino_mall/leg_selected.png")
      p.equipment-type-each-text Leg
</template>

<script>
export default {
  data(){
    return {
      selectPartId:''
    }
  },
  props: {
    propParts: {
      type: String,
      default: ''
    },
    isLoading: {
      type: Boolean,
      default: false
    }
  },
  methods:{
    openShow(part=''){
    	if(this.isLoading){
        this.$showToast('数据加载中，请注意操作间隔')
    		return
      }
      this.selectPartId = part
      this.$emit("changePart",part)
    }
  }
}
</script>

