<template>
  <div class="vk_swipe_item">
    <slot></slot>
  </div>
</template>
<script>
  export default {
    name: 'vk_swipe_item',
    mounted() {
      this.$parent && this.$parent.swipeItemCreated(this)
    },
    destroyed() {
      this.$parent && this.$parent.swipeItemDestroyed(this)
    }
  }
</script>
