<script>
import TopPart from '../common/top_part.vue'
import axios from 'axios'
import cookies from 'cookies-js'
import number from '../common/vk_number.vue'
import utils from '../../utils/_untils'
import formUrlEncoded from 'form-urlencoded'
export default {
  data() {
    return {
      isShowUserGuideMask: false, // 半透明黑色蒙层，每次弹框需开启
      isSecretDialogShow: false, // 显示贺卡弹框
      pageIndex: 1,// 当前页码
      cardsList: [], // 贺卡列表
      seeCardInfo: {},// 查看贺卡对象
      showMore: false,// 更多按钮
      isLoading: false,// 加载状态
      noReadNnumber: 0 // 未读贺卡全部数据
    }
  },
  props: [],
  mounted() {
    sa.track('learning_click', {
      'click_id': 'pc_learning_greeting_cards_see_list'
    })
  },
  beforeDestroy() {},
  methods: {
    closeDialog(opt) {
      this.isShowUserGuideMask = !this.isShowUserGuideMask
      this.isSecretDialogShow=false
    },
    openShow(obj) {
      this.isShowUserGuideMask = !this.isShowUserGuideMask
      this.isSecretDialogShow = true
      this.seeCardInfo = obj
      if(this.seeCardInfo.readStatus!="YES"){
        this.setHasSee()
      }
      sa.track('learning_click', {
        'click_id': 'pc_learning_greeting_cards_open_see'
      })
    },
    // 获取收件列表
    getCardList(){
      this.isLoading = true
      axios.get("/rest/learninggw/api/point/egreetingCardSendReceive/getReceiveList", {
        params: {
          studentId: cookies.get('studentId'),
          start: this.pageIndex,
          limit: 12
        }
      }).then(res => {
        if(res.data.code == 200){
          if(res.data.data.data){
            this.cardsList = this.cardsList.concat(res.data.data.data)
          }else{
            this.cardsList = []
          }

          if(this.pageIndex < res.data.data.totalPages){
            this.pageIndex++
            this.showMore = true
          }else{
            this.showMore = false
          }
        }else{
          this.$showToast('获取已收贺卡数据失败<br>请检查网络~')
        }
        this.isLoading = false
      }).catch(v => {
        this.$showToast('获取已收贺卡数据失败<br>请检查网络~')
        this.isLoading = false
      })
    },
    // 获取未读取数量
    getNoReadNumber(){
      axios.get("/rest/learninggw/api/point/egreetingCardSendReceive/getUnReadCount", {
        params: {
          studentId: cookies.get('studentId')
        }
      }).then(res => {
        if(res.data.code == 200){
          this.noReadNnumber = res.data.data
        }else{
          this.$showToast('获取未读取贺卡数量失败<br>请检查网络~')
        }
      }).catch(v => {
        this.$showToast('获取未读取贺卡数量失败<br>请检查网络~')
      })
    },
    // 更改查看状态
    setHasSee(){
      axios.post("/rest/learninggw/api/point/egreetingCardSendReceive/setRead", formUrlEncoded({
        studentId: cookies.get("studentId"),
        egreetingCardSendReceiveIds: this.seeCardInfo.id
      })).then(res => {
        if(res.data.code == 200){
          let obj= this.cardsList.filter(v=>{
            return v.id==this.seeCardInfo.id
          })
          if(obj && obj[0]){
            obj[0].readStatus="YES"
            this.noReadNnumber = this.noReadNnumber -1
          }
        }else{
          this.$showToast("查看失败了")
        }
      }).catch(() => {
        this.$showToast("查看失败了")
      })
    },
    // 回复
    reply(teacheId=0,cardId=0){
      sa.track('learning_click', {
        'click_id': 'pc_learning_greeting_cards_open_reply'
      })
      this.$router.push({path: `/greeting_cards/${teacheId}/${cardId}`})
    }
  },
  components: {
    TopPart
  },
  created(){
    // 初始化数据
    this.getCardList()
    this.getNoReadNumber()
  },
  filters: {
    dateForm: function(time){
      return utils.formatDate(time)
    }
  }
}
</script>
