<template lang="jade">
  .bang-package-content(:class="isHideAll?'opw-hide':''")
    img.star-an(v-show="isHideAll",v-for="(star,index) in stars",src="../../../img/index/meteorite_2@2x.png",:style="star")
    .top
    .content
      .text() 你恭喜你获得{{impelNumber}}个宝石～
      .new-box
        .new-box-bg
        .number
          number.number-text(ref="numberText",:number="impelNumber")
        img.star(ref="star",v-if="!isHideAll",src="../../../img/index/meteorite_2@2x.png")
      .circle
        .circle-item.animated.zoomIn(v-for="circleItem in circleItems",:style="circleItem")
</template>
<script>
  import number from '../../common/vk_number.vue'
  export default {
    data: function () {
      return {
        stars: [],
        circleItems: [],
        impelNumber: "0",
        isHideAll: false
      }
    },
    components: {
      number
    },
    props: ['number'],
    computed: {},
    mounted () {
      this.initLightAn()
      this.getNumber()
    },
    methods: {
      getNumber(){
        this.impelNumber = this.number + ""
        this.isShow = true
        this.$nextTick(() => {
          this.stars = []
          for (var i = 0; i < 5; i++) {
            this.stars.push({
              top: this.$refs.star.getBoundingClientRect().top + "px",
              left: this.$refs.star.getBoundingClientRect().left - 30 + "px",
              transform: "scale(1.2, 1.2)",
              transitionDelay: i * 0.1 + "s"
            })
          }
        })
        setTimeout(() => {
          this.lightAn()
        }, 200)
        setTimeout(() => {
          this.hide()
        }, 2000)
      },
      initLightAn(){
        this.circleItems = []
        for (var i = 0; i < 10; i++) {
          let ra = Math.random() * 50
          this.circleItems.push({
            top: "160px",
            left: "280px",
            height: ra + "px",
            width: ra + "px",
            backgroundColor: "rgb(0, 221, 151)",
            opacity: Math.random() * 0.7
          })
        }
      },
      lightAn(){
        this.circleItems = []
        for (var i = 0; i < 10; i++) {
          let ra = Math.random() * 50
          this.circleItems.push({
            top: Math.random() * 300 + "px",
            left: Math.random() * 500 + "px",
            height: ra + "px",
            width: ra + "px",
            backgroundColor: "rgb(0, 221, 151)",
            opacity: Math.random() * 0.7
          })
        }
      },
      hide(){
        this.isHideAll = true
        this.$nextTick(() => {
          setTimeout(() => {
            this.stars = []
            for (var i = 0; i < 5; i++) {
              this.stars.push({
                top: "20px",
                right: "30px",
                transform: "scale(0.2, 0.2)",
                transitionDelay: i * 0.1 + "s"
              })
            }
          }, 200)
        })
        setTimeout(() => {
          //this.$emit("boxClose")
        }, 700)
      }
    }
  }
</script>

<style lang="stylus" scoped>
