    text-align: center
    .top
      height: 50%
      margin-bottom: -300px
    .content
      position: relative
      height: 380px
      width: 600px
      margin: auto
      .text
        font-size: 24px
        color: #ffffff

  .new-box
    position: absolute
    width: 600px
    height: 310px
    margin: 30px auto
    box-sizing: border-box
    padding: 50px 255px
    z-index: 2
    .number
      height: 44px
      width: 170px
      margin: auto
      .number-text
        transform: scale(1.5, 1.5)
        transition: all 1s 1s
    .star
      margin: 20px auto
      transition: all 1s
    .star-hide
      position: absolute
      z-index: 10
      left: 200px
      top: 30px
    .new-box-bg
      position: absolute
      background: url("../../../img/index/bg_light.png") no-repeat center
      background-size: 100% 100%
      width: 310px
      height: 310px
      top: 0
      left: 145px
      animation: lightRotate linear 5s infinite

  .circle
    position: absolute
    height: 380px
    width: 600px
    z-index: 1
    .circle-item
      position: absolute
      border-radius: 50%
      transition: all 3s

  .star-an
    position: fixed
    top 30%
    right 45%
    z-index: 120
    transform: scale(1, 1)
    transition: all 0.3s ease

  .opw-hide
    background-color: rgba(23, 2, 62, 0)

  @keyframes lightRotate
    0%
      opacity: 0.5
      transform: rotateZ(0deg) scale(1.2, 1.2)
    50%
      opacity: 1
      transform: rotateZ(180deg) scale(1.7, 1.7)
    100%
      opacity: 0.5
      transform: rotateZ(360deg) scale(1.2, 1.2)
</style>
