        img.guide-bubble-know-1.animated.pulse.infinite(src="../../../img/digireader/guide_iknown@2x.png", @click.stop="step1")
      template(v-if="step==2")
        .jh-gem
          .icon-gem
          .icon-right-btn
          .number 100
        img.guide-bubble-know-2(src="../../../img/digireader/guide_iknown@2x.png", @click.stop="guideHide(false)")
        img.impel-exchange.animated.pulse.infinite(src="../../../img/index/exchange.png",@click.stop="goToGift")
        img.impel-tip-2(src="../../../img/index/bubblebg-2@2x.png")
</template>
<script>
  export default {
    data: function () {
      return {
        step: 1
      }
    },
    components: {},
    props: ['isShowAccessBtn'],
    computed: {},
    mounted () {
    },
    methods: {
      step1(){
        this.step = 2
        this.$playSound("click")
      },
      guideHide(isOut){
        this.$emit("guideClose",isOut)
        setTimeout(() => {
          this.step = 1
        }, 1000)
        this.$playSound("click")
      },
      goToGift(){
        this.guideHide(true)
        this.$router.push("/gift_exchange")
      }
    }
  }
</script>


