    .live-course(:style="{backgroundColor: currentPanelState=='face'?'#ffffff':'#6ad3c6'}")
      transition-group(enter-active-class="animated fadeInDown")
        //正面
        .front-face(key="1",v-if="currentPanelState=='face'")
          .mid
            //课前预习视频
            template(v-if="config.cardStyle=='pre_video'")
              img.pre-video-img(:class="{'video-img-disable':config.aiCourseCard.imageInfo.status==workStatus.undone}",:src="config.aiCourseCard.imageInfo.imgUrl")
            //进教室
            classroom(v-if="config.cardStyle=='classroom'",:classInfo="config.classInfo")
            //flash card
            flash-card(v-if="config.cardStyle=='flash_card'",:cardConfig="config.homeworkInfo")
            //做作业
            homework(v-if="config.cardStyle=='homework'",:cardConfig="config.homeworkInfo")
          //卡片核心按钮
          vk-btn.star-btn(:type="classBtn",:text="config.bottomButton.buttonText",:clickFun="buttonClick")
          .progress
            vk-progress(:pps="pps")
        //背面
        .back-face(key="2",v-if="currentPanelState=='back'")
          back-card(:config="config")
    .record-course
      img.video-img(:class="{'video-img-disable':config.aiCourseCard.imageInfo.status==workStatus.undone}",:src="config.aiCourseCard.imageInfo.imageUrl")
      vk-btn(:type="videoBtn",:text="config.aiCourseCard.bottomButton.buttonText",:clickFun="videoClick", alertText="直播课结束后才可查看哦~")
      .video-progress
        vk-progress(:pps="videoPps")
</template>
