<template lang="jade">
  .card
    template(v-if="courseData.isLocked")
      .card-item.card-ball(:class="'card-extension-' + cardSequence", @mouseenter="ballBubbleHintShow", @mouseleave="ballBubbleHintHide")
        span {{ courseData.sequence }}
        .test_lesson_label_on_ball(v-if="isTestLesson")
        .bubble-hint(v-show="isBubbleHintShow") 预约课程后即可解锁
    template(v-else)
      .card-item(:class="['card-extension-' + cardSequence, isOrder ? 'card-normal' : 'disorder']", @click="detailTrigger", ref='cardItem')
        .j-init-booked-status-lesson(v-if=" courseData.onlineStatus == 'BOOKED' ")
        .j-as-schedule-item(v-if="courseData.lessonStatus==5")
        .test_lesson_label(v-if="isTestLesson")
        .card-number(v-if="isOrder") {{ courseData.sequence }}
        img.teacher-head(v-if="courseData.detailAvatar", :src="courseData.detailAvatar?courseData.detailAvatar:null")
        img.teacher-head(v-else="!courseData.detailAvatar", src="../../../img/user-imag@2x.png")
        template(v-if="courseData.showStatus == 1")
          .card-star(:class="'star-' + courseData.starNumber")
        template(v-else)
          .card-time-con
            span {{courseData.detailTimeString.split(' ')[0]}}
            strong {{courseData.detailTimeString.split(' ')[1]}}
        .text-area(@scroll.stop="doNothing", @mousewheel.stop="doNothing", @DOMMouseScroll.stop="doNothing")
          i.placeholder
          p {{ courseData.detailTitle }}
        .button-container
          template(v-for="item in courseData.pathButtonList")
            .button-bg-container.grey(v-if="item.buttonStatus == 'GRAY'", @click.stop="doNothing") {{item.buttonText}}
            .button-bg-container.grey-alert(v-else-if="item.buttonStatus === 'GRAY_ALERT'", @click.stop="popAlert(item.buttonExtra.alertText)") {{item.buttonText}}
            .button-bg-container.normal(v-else-if="['READ_BOOK'].indexOf(item.buttonFunction) > -1", @click.stop="goBookDetail(item.buttonExtra.bookId, item.buttonExtra.bookCode)") {{item.buttonText}}
            .button-bg-container.normal(v-else, @click.stop="courseButtonHandler(item)") {{item.buttonText}}
</template>
<script>
  import Vue from 'vue'
  import axios from 'axios'
  import cookies from 'cookies-js'
  import utils from '../../../utils/_untils.js'
  import Course from '../../common/course/Course.js'

  export default {
    data() {
      return {
        course: this.courseData,
        cardSequence: this.courseIndex + 1,
        serverTime_: parseInt(this.serverTime),
        WINTER_CAMP_BEGINNER: 'Winter Camp Beginner 2018',
        cardItemClassName: '',
        islandTypeConst: {
          openCourse: 1,
          toefl: 2,
          winterCamp: 3,
          summerCamp: 4,
          otherCourse: 5,
          phonics: 6,
          pronunciation: 7,
          vov: 8,
          freeTalk: 9
        },
        isBubbleHintShow: false,
        isPreVideo: false, //预习是否是预习视频
        isTestLesson: false,
        isOrder: false, //是否是有序课
        isRecentAsScheduled: true
      }
    },
    props: ['courseData',
      'courseIndex',
      'serverTime',
      'islandType'
      // 'positionIndex'
    ],
    mounted() {
      // console.log(this.positionIndex);
      // let className = 'card-extension-'+(this.positionIndex+1)
      // console.log(className)
      // let offsetLeft = document.getElementsByClassName(className)
      // console.log(offsetLeft)
      // let selfClientWidth = document.body.clientWidth/2
      // if (offsetLeft > selfClientWidth) {
      //   console.log(offsetLeft - selfClientWidth + 125)
      // }
      this.differenceHandler()
    },
    computed: {
    },
    methods: {
      courseButtonHandler(buttonData) {
        Course.courseButtonClick({
          type: buttonData.buttonFunction,
          buttonExtra: buttonData.buttonExtra,
          clickId: buttonData.clickId + '_path_card',
          onlineClassId: this.courseData.onlineClassId,
          lessonId: this.courseData.lessonId,
          courseId: this.courseData.courseId, 
          curriculumVersion: this.courseData.curriculumVersion,
          bookType: this.courseData.bookType,
          lessonNum: this.courseData.lessonNumber.match(/\d+/g)[0]
        })
      },
      differenceHandler() {
        //设置有序无序
        /*
          openCourse: 1,
          toefl: 2,
          winterCamp: 3,
          summerCamp: 4,
          otherCourse: 5,
          phonics拼读: 6,
          pronunciation发音: 7,
          vov唱: 8,
          freeTalk聊: 9
          steam: 13
        */
        if([2,3,4,6,7,8,9,13].indexOf(this.islandType) > -1) {
          this.isOrder = true
        }
      },
      goBookDetail(bookId, bookCode) {
        this.$router.push({path: `/dgl/digitalLibrary/detail/${bookId}/${bookCode}`})
      },
      enterReplay(onlineClassId, type) {
        sa.track('learning_click', {'click_id': 'pc_learning_' + this.islandType + '_replay_on_card'})
        if (type == 'PUBILC_REPLAY') {
          window.open('/router/learning/replay/openclass?onlineClassId=' + onlineClassId)
        } else {
          window.open('/router/learning/replay/major?onlineClassId=' + onlineClassId)
        }
      },
      ballBubbleHintShow() {
        this.isBubbleHintShow = true
      },
      ballBubbleHintHide() {
        this.isBubbleHintShow = false
      },
      doNothing() {
        return
      },
      // gray_alert 按钮弹出提示方法
      popAlert(alertText) {
        this.$showToast(alertText)
      },
      detailTrigger() {
        this.$emit("detailTrigger")
      }
    }
  }
</script>

