<template lang="jade">
  .island-page-wrapper
    top-part(:title_cn="titleCh", :title_eng="titleEn", :gobackurl="'/extension'")
    vertical-scroll(:marginT="'-204px'")
      .vertical-scroll-box
        .island(v-for = "(items, index) in cardList", :class="[{'has-bridge': index < cardList.length - 1}, ui.islandTypeClass]")
          template(v-for = "(item, index) in items")
            path-card(@detailTrigger="showDetail(item.onlineClassId, item.lessonId, item.workType)", :island-type="planetNum", :course-data="item", :course-index="index", :server-time="serverTime", @trialDialogTriger="trialDialogControl")
    new-course-detail(:isCourseDetailShow="isCourseDetailChildShow", @courseDetailLayerTriger="closeCourseDetail", :courseDetailDataProp="courseDetailData", :planetNumProp="planetNum", @trialDialogTriger="trialDialogControl")
    course-switch
    move-background
    .homeWorkRevise-container(v-if="homeWorkRevise && homeWorkRevise.show",@click = 'changeTipShow')
      .homeWorkIcon
        .errNumber {{homeWorkRevise.reviseNum}}
        transition(name="fade")
          .tips(v-if="tipShow") 错题都改完了~
      .homeWorkRevise-name(@click = 'changeTipShow') {{homeWorkRevise.text}}
</template>
<script>
/** 
 *  
 *  @PlanetNumber:
 *  public: 1
 *  toefl: 2
 *  winter: 3
 *  summer: 4
 *  others: 5
 *  phonics: 6
 *  pronun: 7
 *  vov: 8
 *  freetalk: 9
 *  class_major: 10
 *  class_major2016: 11
 *  class_esl: 12
 *  濒危动物小使者: 13
 *  escalate: 14
 *  高阶主修课: 15
 *  互动课堂: 16
 *  mvp test course 1: 17
 *  mvp test course 2: 18
 *  创作你的节奏: 19
 *  水果家族的秘密: 20
 *  开启你的游戏创意: 21
 * 
 **/
import Vue from 'vue'
import TopPart from '../common/top_part.vue'
import axios from 'axios'
import cookies from 'cookies-js'
import Velocity from 'velocity-animate'
import moveBackground from '../common/move_background.vue'
import newCourseDetail from '../common/course_detail.vue'
import courseSwitch from './minor_switch.vue'
import verticalScroll from '../common/vertical_scroll.vue'
import pathCard from './minor_card.vue'
import vkDialog from '../common/vk_dialog_trial.vue'
import model from "../common/plugins/Model"
Vue.use(model)
export default {
  data: function () {
    return {
      titleCh: '',
      titleEn: '',
      planetNum: parseInt(this.$route.params.planetNum),
      minorType: this.$route.params.minorType,
      curriculumId: this.$route.params.curriculumId,
      curriculumType: this.$route.params.curriculumType,
      isCourseDetailChildShow: false,
      courseDetailData: {},
      tipShow: false,
      homeWorkRevise:'',
      //重构字段
      cardList: [],
      serverTime: 0,
      ui:{
        islandTypeClass: 'island-type-' + this.$route.params.planetNum
      }
    }
  },
  components: {
    TopPart,
    newCourseDetail,
    courseSwitch,
    moveBackground,
    verticalScroll,
    pathCard
  },
  mounted() {
    this.getLessons()
  },
  methods: {
    changeTipShow(){
      if(this.homeWorkRevise.reviseNum==0 && !this.tipShow){
        this.tipShow = true
        setTimeout(()=>{
          this.tipShow = false
        },2000)
      } else if(this.homeWorkRevise.reviseNum!=0){
        sa.track('learning_click', {'click_id': 'pc_learning_minor_jump_to_questionsRevise_button'})
        window.location.href = this.homeWorkRevise.reviseUrl+ "&back_url=" + encodeURIComponent(document.location.href)//@back_url 用于作业端返回
      }
    },
    trialDialogControl(func) {
      this.$modelShow({
        model: vkDialog,
        title: "为了确保试听效果，请您阅读如下注意事项并进行检查确认",
        buttonText: "知道了",
        enter: () => {
          this.$modelClose()
          func()
        },
        closeDialog: () => {
          this.$modelClose()
        }
      })
    },
    showDetail(onlineClassId, lessonId, workType) {
      axios.get('/rest/learninggw/api/pc/path/minor/getMinorPathDetail', {
        params: {
          studentId: cookies.get("studentId"),
          lessonId: lessonId,
          onlineClassId: onlineClassId
        }
      })
        .then((res) => {
          this.courseDetailData = res.data.data
          this.isCourseDetailChildShow = true
        })
        .catch((err) => {
          alert('网络有点问题，检查一下网络连接吧')
        })
    },
    closeCourseDetail() {
      this.isCourseDetailChildShow = false
    },
    getLessons() {
      let url
      let param

      if (this.curriculumId) {
        url = '/rest/learninggw/api/pc/path/minor/getMinorPath'
        param = {
          studentId: cookies.get("studentId"),
          planetNumber: this.planetNum,
          curriculumId: this.curriculumId,
          curriculumType: this.curriculumType
        }
      } else {
        url = '/rest/learninggw/api/pc/path/minor/getMinorPathDefault'
        param = {
          studentId: cookies.get("studentId"),
          planetNumber: this.planetNum,
          minorType: this.minorType
        }
      }
      
      axios.get(url, {
        params: param
      }).then((res) => {
        let data = res.data.data
        if(res.data.code != 200) {
          alert('网络有点问题，检查一下网络连接吧')
          return
        }
        this.titleCh = data.title
        this.homeWorkRevise = data.homeWorkRevise
        if (data.subTitle && data.subTitle != 'N/A') {
          this.titleEn = data.subTitle
        } else {
          this.titleEn = ''
        }
        
        this.serverTime = data.serverTime
        let list = this.chunk(data.pathLessonList, 3)
        for(let i = 0; i < list.length; i++) {
          this.cardList.push(list[i])
        }
      })
    },
    chunk(array, subArrayLen) {
      if(!array){
        return
      }
      var result = []
      for (var i = 0, len = array.length; i < len; i += subArrayLen) {
        result.push(array.slice(i, i + subArrayLen))
      }
      return result
    }
  }
}
</script>
