<template lang="jade">
	.mask
	  .vk-net-error
	    img(src="../../../img/common/net-error.png")
	    .vk-net-info {{errorMsg}}
	    .vk-btn-reload(@click="errorReload") 刷新重试
</template>
<script>
export default {
  data: function () {
    return {
      errorMsg: '糟糕！网络链接出现问题'
    }
  },
  props: ['msg'],
  components: {},
  computed: {},
  mounted() {
    if (!this.msg) {
      this.errorMsg = '糟糕！网络链接出现问题'
    }else{
      this.errorMsg = this.msg
    }
  },
  methods: {
    errorReload(){
      this.$emit('errorReload')
    }
  }
}
</script>

