<template lang="jade">
  .pd-detail-thumb-item
    .avatar
      img(src = "../../../img/common/avatar/default.png", v-if = "!rank.avatarUrl && (rank.avatar == 'default'|| !rank.avatar)")
      img(src = "../../../img/common/avatar/boy_1.png", v-if = "!rank.avatarUrl && rank.avatar == 'boy_1'")
      img(src = "../../../img/common/avatar/boy_2.png", v-if = "!rank.avatarUrl && rank.avatar == 'boy_2'")
      img(src = "../../../img/common/avatar/boy_3.png", v-if = "!rank.avatarUrl && rank.avatar == 'boy_3'")
      img(src = "../../../img/common/avatar/boy_4.png", v-if = "!rank.avatarUrl && rank.avatar == 'boy_4'")
      img(src = "../../../img/common/avatar/girl_1.png", v-if = "!rank.avatarUrl && rank.avatar == 'girl_1'")
      img(src = "../../../img/common/avatar/girl_2.png", v-if = "!rank.avatarUrl && rank.avatar == 'girl_2'")
      img(src = "../../../img/common/avatar/girl_3.png", v-if = "!rank.avatarUrl && rank.avatar == 'girl_3'")
      img(src = "../../../img/common/avatar/girl_4.png", v-if = "!rank.avatarUrl && rank.avatar == 'girl_4'")
      // 自定义头像
      img(:src = "rank.avatarUrl", v-if="rank.avatarUrl")
    img.medal.excellent(v-if="rank.awardsImage", :src="rank.awardsImage")
    .name
      | {{rank.stuEnglishName}}
      span(v-if="rank.studentId == studentId")
    .title {{rank.name}}
</template>
<script>
  import cookies from 'cookies-js'
export default {
    data() {
      return {
        studentId: cookies.get('studentId'),
        excellent: false,
        popular: false
      }
    },
    props:['rank','rankName'],
    components: {
    },
    mounted() {
      if (this.rankName.indexOf('优秀') > -1) {
        this.excellent = true
      }
      if (this.rankName.indexOf('人气') > -1) {
        this.popular = true
      }
    },
    methods: {
    }
}
</script>
