<template lang="jade">
  .select-box
      .select-label
        .select-text(@click="showSelect")
          | {{val}}
          span(:class="{'show':show}")
      ul.list.animated.fadeIn(v-show="show")
        li(v-for="(item, index) in list",v-if="checked !== item.id",@click="selectValue(item.levels,item.name,item.id)") {{item.name}}
</template>
<script>
export default {
  data() {
    return {
      val: '',
      btnValue: false,
      show: this.selectShow,
      checked: ''
    }
  },
  props:['list','focus','selectShow'],
  components: {
  },
  created(){
    if (this.list && this.list[0]) {
      this.val = this.list[0].name
      this.checked = this.list[0].id
    }

  },
  mounted() {
  },
  methods: {
    showSelect(){
      this.$playSound("click")
      if (this.list.length > 1 ) {
        this.show = !this.show
      }
    },
  	selectValue(level,name,id){
      this.$playSound("click")
      sa.track('learning_click', {'click_id': 'pc_learning_activity_competitionArena_competition_ChooseZone'})
  		this.val = name
      this.checked = id
  		this.show = false
      this.$emit("select",id)
  	}
  }
}
</script>
