import axios from 'axios'
import formurlencoded from 'form-urlencoded'

const apiurl = '/rest/learninggw'

function $get(urls, date){
  let url = `${apiurl}${urls}`
  return axios.get(url,{
    params: date
  })
}

function $post(urls, data, isUrlencoded=true){
  let date = ''
  if(isUrlencoded){
    date = formurlencoded(data)
  }else{
    date = data
  }
  let url = `${apiurl}${urls}`
  return axios.post(url, date)
}

function $all(fn1, fn2){
  return axios.all([fn1, fn2])
}

export {
  $get,
  $post,
  $all
}
