<script>
import myDialog from "../common/dialog.vue"

export default {
  data() {
    return {
      isFilterShow: false,
      isShowDialog: false,
      levelIdCollection: this.levelIds,
      subjectIdCollection: this.subjectIds,
      levelNames:['All Levels','Level 1/2','Level 3','Level 4','Level 5','Level 6'],
      subjectNames: [['All Types','全部类型'], ['Mathematics','数学'], ['Science','科学'], ['Social Studies','社会学']],
      levelSelected: this.currentLevelId,
      subjectSelected: 'allSubjects',
      filterArray: [],
      filterBarLevelIconClassName: 'level-all',
      filterBarSubjectIconClassName: 'subject-all',
      filterBarLevelInfoMap: {},
      filterBarSubjecInfoMap: {},
      levelIconClassCollection: ['level-all','level-1','level-3','level-4','level-5','level-6'],
      subjectIconClassCollection: ['subject-all','subject-math','subject-science','subject-social'],
      filterBarLevelName: 'All Levels',
      filterBarSubjectName: 'All Types',
      isDeleteButtonForbidden: true,
      isShowUserGuideSecond_: this.isShowUserGuideSecond
    }
  },
  components: {
    myDialog
  },
  props: ['levelIds', 'subjectIds', 'isShowUserGuideSecond', 'currentLevelId'],
  mounted() {

  },
  watch: {
    levelIds(curVal) {
      this.setFilterBarLevelIconMap()
    },
    subjectIds(curVal) {
      this.setFilterBarSubjectIconMap()
    },
    currentLevelId(curVal) {
      this.levelSelected = this.currentLevelId
      this.setShowLevelAndSubject()
      if(this.levelSelected != 'allLevels') {
        this.isDeleteButtonForbidden = false
      }
    },
    isShowUserGuideSecond(curVal) {
      this.isShowUserGuideSecond_ = this.isShowUserGuideSecond
    }
  },
  methods: {
    closeUserGuideLayer() {
      this.isShowUserGuideSecond_ = false
      this.$emit('closeUserGuideBUS')
    },
    cancelDialog(){
      this.isShowDialog = !this.isShowDialog
    },
    confirmDeleteFilter() {
      this.isShowDialog = true
    },
    deleteFilter() {
      this.filterArray = []
      this.filterBarLevelIconClassName = this.filterBarLevelInfoMap['allLevels'].iconClassName
      this.filterBarSubjectIconClassName = this.filterBarSubjecInfoMap['allSubjects'].iconClassName
      this.filterBarLevelName = this.filterBarLevelInfoMap['allLevels'].levelName
      this.filterBarSubjectName = this.filterBarSubjecInfoMap['allSubjects'].subjectName
      this.levelSelected = 'allLevels'
      this.subjectSelected = 'allSubjects'
      this.setBookMarker()
      this.cancelDialog()
      this.$emit('filterEvent', this.filterArray)
      this.isDeleteButtonForbidden = true
    },
    setFilterBarLevelIconMap() {
      for(let i = 0; i < this.levelIdCollection.length; i++) {
        this.filterBarLevelInfoMap[this.levelIdCollection[i]] = {
          'iconClassName': this.levelIconClassCollection[i], 
          'levelName': this.levelNames[i]
        }
      }
    },
    setFilterBarSubjectIconMap() {
      this.subjectIdCollection.vkForEach((item, index) => {
        this.filterBarSubjecInfoMap[item] = {
          'iconClassName': this.subjectIconClassCollection[index],
          'subjectName': this.subjectNames[index][0]
        }
      })
    },
    submitFilter() {
      this.filterArray = []
      this.filterArray.push(this.levelSelected)
      this.filterArray.push(this.subjectSelected)
      this.setShowLevelAndSubject()
      this.closeFilterLayer()
      this.setBookMarker()
      this.$emit('filterEvent', this.filterArray)
      if(this.filterArray.length==0) {
        this.isDeleteButtonForbidden = true
      } else {
        this.isDeleteButtonForbidden = false
      }
    },
    setShowLevelAndSubject() {
      this.filterBarLevelIconClassName = this.filterBarLevelInfoMap[this.levelSelected].iconClassName
      this.filterBarSubjectIconClassName = this.filterBarSubjecInfoMap[this.subjectSelected].iconClassName
      this.filterBarLevelName = this.filterBarLevelInfoMap[this.levelSelected].levelName
      this.filterBarSubjectName = this.filterBarSubjecInfoMap[this.subjectSelected].subjectName
    },
    setBookMarker() {
      this.$emit('setBookMarkerBUS', this.subjectSelected)
    },
    openFilterLayer() {
      this.isFilterShow = true
    },
    closeFilterLayer() {
      this.isFilterShow = false
    },
    selectLevel(levelId) {
      this.levelSelected = levelId
      sa.track('learning_click', {'click_id': 'pc_learning_digital_library_partnered_books_filter_level_id_' + levelId})
    },
    selectSubject(subjectId) {
      this.subjectSelected = subjectId
      sa.track('learning_click', {'click_id': 'pc_learning_digital_library_partnered_books_filter_subjectId_id_' + subjectId})
    }
  }
}
</script>
