<script>
import myDialog from "../common/dialog.vue"
import utils from '../../../utils/_untils.js'
import Velocity from 'velocity-animate'
import axios from 'axios'

export default {
  data() {
    return {
      isFilterShow: false,
      isShowDialog: false,
      bounceAnimationFlag: false,
      levelNames:['All Levels', 'Level 1', 'Level 2', 'Level 3', 'Level 4', 'Level 5', 'Level 6'],
      levelIdSelected: this.currentLevelId,
      typeIdSelected: 0,
      filterArray: [],
      filterBarLevelInfoMap: {},
      filterBarSubjecInfoMap: {},
      isDeleteButtonForbidden: true,
      isShowUserGuideSecond_: this.isShowUserGuideSecond,
      timeouts:[],
      scollRunning: false,
      typeBoxTranslateX: 0,
      isShowLeftScrollBtn: false,
      isShowRightScrollBtn: true,
      currentLevel: {
        iconUrl:'',
        name: ''
      },
      typeSelected: {
        iconUrl:' ',
        name: ''
      },
      levelAndTypeMap:{},
      typesWithContent: this.typeInfoObj
    }
  },
  components: {
    myDialog
  },
  props: ['levelInfoObj', 'typeInfoObj', 'isShowUserGuideSecond', 'currentLevelId', 'levelSmallIconCollection', 'typeSmallIconCollection'],
  mounted() {
  },
  watch: {
    typesWithContent(curVal) {
      this.typesWithContent = curVal
    },
    levelInfoObj(curVal) {
    },
    typeInfoObj(curVal) {
      this.setShowType()
    },
    currentLevelId(curVal) {
      this.levelIdSelected = curVal
      this.$nextTick(() => {
        this.setShowLevel(curVal)
        this.getLevelAndTypeMap()
      })
    },
    isShowUserGuideSecond(curVal) {
      this.isShowUserGuideSecond_ = this.isShowUserGuideSecond
    }
  },
  methods: {
    getLevelAndTypeMap() {
      axios.get('/rest/learninggw/api/pc/service/digitlib/getLevelTypeMap')
        .then((res) => {
          this.levelAndTypeMap = res.data.data
          this.setTypeWithContent()
        })
        .catch((err) => {})
    },
    scrollLeft(e) {
      this.scrollAnimation(e, 'left')
    },
    scrollRight(e) {
      this.scrollAnimation(e, 'right')
    },
    scrollAnimation(e, direction) {
      if( !this.scollRunning ) {
        this.scollRunning = true //开始滚动
        if( direction == 'right' ) {
          this.typeBoxTranslateX -= 196
        } else {
          this.typeBoxTranslateX += 196
        }
        Velocity(this.$refs.typeBox, { translateX: this.typeBoxTranslateX + 'px' }, { duration: 300})
        clearTimeout(this.timeouts)
        this.timeouts = []
        utils.removeClass(e.target.firstChild, 'move')
        e.target.firstChild.style.top = e.offsetY + 'px'
        e.target.firstChild.style.left = e.offsetX + 'px'
        e.target.firstChild.className += ' move'
        this.timeouts = setTimeout(()=>{
          utils.removeClass(e.target.firstChild, 'move')
          if(this.typeBoxTranslateX >= 0) {
            this.isShowLeftScrollBtn = false
          } else {
            this.isShowLeftScrollBtn = true
          }
          if( Math.abs(this.typeBoxTranslateX) >= this.$refs.typeBox.offsetWidth - 196*4) { //容器能装下4个类别
            this.isShowRightScrollBtn = false
          } else {
            this.isShowRightScrollBtn = true
          }
          this.scollRunning = false //结束滚动
        }, 400)
      }
    },
    resetScroll() {
      this.typeBoxTranslateX = 0
      Velocity(this.$refs.typeBox, { translateX: '0px' }, { duration: 0})
    },
    closeUserGuideLayer() {
      this.isShowUserGuideSecond_ = false
      this.$emit('closeUserGuideBUS')
    },
    cancelDialog(){
      this.isShowDialog = !this.isShowDialog
    },
    confirmDeleteFilter() {
      this.isShowDialog = true
    },
    deleteFilter() {
      this.resetScroll()
      this.filterArray = []
      this.selectLevel(0)
      this.selectSubject(0)
      this.setShowLevel()
      this.setShowType()
      this.setBookMarker()
      this.cancelDialog()
      this.$emit('filterEvent', this.filterArray)
      this.isDeleteButtonForbidden = true
    },
    submitFilter() {
      this.filterArray = []
      this.filterArray.push(this.levelIdSelected)
      this.filterArray.push(this.typeIdSelected)
      this.setShowLevel()
      this.setShowType()
      this.closeFilterLayer()
      this.setBookMarker()
      this.$emit('filterEvent', this.filterArray)
      if(this.filterArray.length == 0) {
        this.isDeleteButtonForbidden = true
      } else {
        this.isDeleteButtonForbidden = false
      }
    },
    setShowLevel() {
      for(let i = 0; i < this.levelInfoObj.length; i++) {
        if(this.levelInfoObj[i].id == this.levelIdSelected) {
          this.currentLevel.iconUrl = this.levelInfoObj[i].imgSmallUrl
          if(this.levelIdSelected != 0) {
            this.currentLevel.name = 'Level ' + this.levelInfoObj[i].name.match(/L\d/).toString().match(/\d/).toString()
          } else {
            this.currentLevel.name = 'All Levels'
          }
        }
      }
    },
    setShowType() {
      let keys = Object.keys(this.typeInfoObj)
      for(let i = 0; i < keys.length; i++) {
        if(this.typeInfoObj[keys[i]].id == this.typeIdSelected) {
          this.typeSelected.iconUrl = this.typeInfoObj[keys[i]].imgSmallUrl
          this.typeSelected.name = this.typeInfoObj[keys[i]].name
        }
      }
    },
    setBookMarker() {
      this.$emit('setBookMarkerBUS', this.typeIdSelected)
    },
    openFilterLayer() {
      this.$playSound("click")
      this.bounceAnimationFlag = true //引入浮层弹入动画class
      this.isFilterShow = true
    },
    closeFilterLayer() {
      this.$playSound("click")
      this.bounceAnimationFlag = false //引入浮层弹出动画class
      setTimeout(() => {
        this.isFilterShow = false
      }, 400)
    },
    selectLevel(levelId) {
      sa.track('learning_click', {'click_id': 'pc_learning_digital_library_vipkid_origin_filter_level_id_' + levelId})
      this.levelIdSelected = levelId
      this.setTypeWithContent()
    },
    selectSubject(subjectId) {
      sa.track('learning_click', {'click_id': 'pc_learning_digital_library_vipkid_origin_filter_subjectId_id_' + subjectId})
      this.typeIdSelected = subjectId
    },
    setTypeWithContent() {
      this.typesWithContent = {}
      let typeIdsWithContent = []
      let levelKeys = Object.keys(this.levelAndTypeMap)
      for(let i = 0; i < levelKeys.length; i++) {
        if(levelKeys[i] == this.levelIdSelected) {
          typeIdsWithContent = this.levelAndTypeMap[levelKeys[i]]
        }
      }
      let typeInfoObjKeys = Object.keys(this.typeInfoObj)
      for(let i = 0; i < typeIdsWithContent.length; i++) {
        for(let j = 0; j < typeInfoObjKeys.length; j++) {
          if(typeInfoObjKeys[j] == typeIdsWithContent[i]) {
            this.$set(this.typesWithContent, typeIdsWithContent[i], this.typeInfoObj[typeInfoObjKeys[j]])
          }
        }
      }

      this.resetScroll()
      let keys = Object.keys(this.typesWithContent)
      if(keys.length > 4) {
        this.isShowLeftScrollBtn = false
        this.isShowRightScrollBtn = true
      } else {
        this.isShowLeftScrollBtn = false
        this.isShowRightScrollBtn = false
      }

    }
  }
}
</script>
