<template lang="jade">
  .record-book-trips-layer
    .book-record-list
      .book-item(
      v-for="item in bookList",
      v-bind:class='{"haveReorded":item.haveReorded,"noRecorded":item.noReorded,"recording":item.isRecording}'
      ) {{item.num}}
    .book-record-list-trips
</template>
<script>
  export default {
    data(){
      return {
        bookList: []
      }
    },
    methods: {
      setBookList(){
        let length = 26
        for (let i = 0; i < length; i++) {
          let listItem = {}
          if (i == 12 || i == 13) {
            listItem.isRecording = true
          } else if (i < 12) {
            listItem.haveReorded = true
          } else {
            listItem.noReorded = false
          }
          listItem.num = i + 1
          this.bookList.push(listItem)
        }
      }
    },
    mounted(){
      this.setBookList()
    }
  }
</script>
