/* 全局状态构造函数 */
function globalState(){
  this.globalInfo = {}
}
globalState.prototype.$set = function(name, value){
  this[name] = value
}
globalState.prototype.$get = function(name){
  return this[name]
}
globalState.prototype.setItem = function(nameSpase, name, value){
  if(nameSpase === 'g' || nameSpase === "global"){
    this.globalInfo[name] = value
    return
  }
  this[nameSpase] = this[nameSpase] || {}
  this[nameSpase][name] = value
}
globalState.prototype.getItem = function(nameSpase, name){
  if(nameSpase === 'g' || nameSpase === 'global'){
    return this.globalInfo[name]
  }
  if(this[nameSpase]){
    return this[nameSpase][name]
  }
  return undefined
}
let storage = new globalState()

export default storage
  
